function snip(s) {
    const regexes = [
        /^START(.+)/ms,
        /(.+?)\nEND$(.+)/ms,
        /BETWEEN(.+?)\n\n(.+)/ms,
    ]
    return imatch(s, regexes)
}
function lexerTest(s) {
    const scanner = new LineScanner(LineScannerBlocks)
    const tokens = scanner.scan(s)
    const root = parser(tokens)
    return root.toString()
}
